const chunk = $input.first().json;
const task = $('Get Enhancement Tasks1').first().json;
console.log(`Processing chunk ${chunk.chunk_id} from ${chunk.original_name}`);
const content = chunk.content || '';
const contentType = chunk.content_type || 'text';
// Generate enhancement prompt based on content type
let enhancementPrompt = '';
if (contentType === 'table' || content.includes('|') || /	.*	/.test(content)) {
  enhancementPrompt = `Improve this table data:

"${content}"

Tasks:
1. Fix OCR errors in table structure
2. Standardize formatting
3. Preserve all numerical data exactly
4. Improve readability

Return ONLY the improved table content.`;
} else {
  enhancementPrompt = `Improve this text:

"${content}"

Tasks:
1. Fix OCR errors and typos
2. Improve grammar and sentence structure
3. Preserve all factual information
4. Use professional language
5. Keep the same length approximately

Return ONLY the improved text.`;
}
// Generate categorization prompt
const categorizationPrompt = `Extract metadata from this content:

"${content.substring(0, 800)}..."

Respond in JSON format:
{
  "categories": ["category1", "category2"],
  "key_topics": ["topic1", "topic2", "topic3"],
  "extracted_metadata": {
    "keywords": ["keyword1", "keyword2"],
    "entities": ["entity1", "entity2"],
    "summary": "brief summary",
    "document_type": "type"
  },
  "detected_references": ["ref1", "ref2"],
  "business_relevance": "high|medium|low"
}";
return [{
  chunk_id: chunk.chunk_id,
  document_id: chunk.document_id,
  original_content: content,
  content_type: contentType,
  chunk_index: chunk.chunk_index,
  page_number: chunk.page_number,
  original_name: chunk.original_name,
  enhancement_prompt: enhancementPrompt,
  categorization_prompt: categorizationPrompt,
  processing_start: new Date().toISOString(),
  queue_id: task.queue_id
}];

const chunk = $('Prepare LLM Processing1').first().json;
const enhancement = $('LLM Enhancement1').first().json;
const categorization = $('LLM Categorization1').first().json;
console.log('Combining LLM results for chunk:', chunk.chunk_id);
// Parse LLM responses safely
function safeParseLLMResponse(response, fallback = {}) {
  try {
    const content = response.response || response.text || '';
    // Try to extract JSON from the response
    const jsonMatch = content.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      return JSON.parse(jsonMatch[0]);
    }
    return fallback;
  } catch (e) {
    console.warn('Failed to parse LLM response:', e.message);
    return fallback;
  }
}
// Parse categorization results
const categorizationResult = safeParseLLMResponse(categorization, {
  categories: [],
  key_topics: [],
  extracted_metadata: { keywords: [], entities: [], summary: '', document_type: 'unknown' },
  detected_references: [],
  business_relevance: 'medium'
});
// Get enhanced content (plain text response)
const enhancedContent = enhancement.response || enhancement.text || chunk.original_content;
// Calculate confidence score
const confidence = Math.min(1.0, (
  0.6 + // base confidence
  (enhancedContent.length > chunk.original_content.length * 0.8 ? 0.2 : 0) +
  (categorizationResult.categories.length > 0 ? 0.1 : 0) +
  (categorizationResult.key_topics.length > 0 ? 0.1 : 0)
));
// Prepare enhanced chunk data for new enhanced_chunks table
const enhancedChunk = {
  chunk_id: chunk.chunk_id,
  document_id: chunk.document_id,
  original_chunk_id: chunk.chunk_id,
  chunk_index: chunk.chunk_index,
  // Enhanced content
  enhanced_content: enhancedContent.trim(),
  original_content: chunk.original_content,
  // LLM extracted data (as JSON strings)
  categories: JSON.stringify(categorizationResult.categories || []),
  extracted_metadata: JSON.stringify(categorizationResult.extracted_metadata || {}),
  detected_references: JSON.stringify(categorizationResult.detected_references || []),
  key_topics: JSON.stringify(categorizationResult.key_topics || []),
  // Content structure
  content_type: chunk.content_type || 'text',
  page_number: chunk.page_number,
  // LLM processing info
  enhancement_model: process.env.DEFAULT_MODEL || 'mistral',
  confidence_score: confidence,
  processing_time: Date.now() - new Date(chunk.processing_start).getTime(),
  // Quality control
  quality_score: Math.min(1.0, confidence + 0.1),
  manual_review_needed: confidence < 0.6,
  // Additional metadata
  business_relevance: categorizationResult.business_relevance || 'medium',
  queue_id: chunk.queue_id
};
console.log(`Enhanced chunk prepared: confidence=${confidence}, quality=${enhancedChunk.quality_score}`);
return [enhancedChunk];

const enhanced = $('Save Enhanced Chunk1').first().json;
const completion = $('Check Completion1').first().json;
const chunk = $('Combine Results1').first().json;
console.log('=== CHUNK ENHANCED ===');
console.log('Enhanced Chunk ID:', enhanced.id);
console.log('Document ID:', chunk.document_id);
console.log('Confidence Score:', enhanced.confidence_score);
console.log('Quality Score:', enhanced.quality_score);
console.log('Progress:', `${completion.enhanced_chunks}/${completion.total_chunks}`);
console.log('Task Status:', completion.task_status);
console.log('Processing Time:', chunk.processing_time, 'ms');
console.log('=====================');
return [{
  success: true,
  enhanced_chunk_id: enhanced.id,
  document_id: chunk.document_id,
  confidence_score: enhanced.confidence_score,
  quality_score: enhanced.quality_score,
  progress: `${completion.enhanced_chunks}/${completion.total_chunks}`,
  task_status: completion.task_status,
  processing_time_ms: chunk.processing_time,
  message: `Enhanced chunk ${chunk.chunk_index} - ${completion.task_status}`
}];

console.log('=== NO ENHANCEMENT TASKS ===');
console.log('Time:', new Date().toISOString());
console.log('Status: All documents enhanced or no pending enhancement tasks');
console.log('============================');
return [{
  message: 'No enhancement tasks found',
  timestamp: new Date().toISOString(),
  status: 'idle'
}];

const input = $input.first().json;
// Output all input data
console.log('üîç Input data:', JSON.stringify(input, null, 2));
// If body is nested
if (input.body) {
  console.log('üì¶ body:', JSON.stringify(input.body, null, 2));
}
return [input];
